import React, { useEffect, useState } from 'react'
import { View, Text, SafeAreaView, ScrollView } from 'react-native'

// sections
import SearchBar from '../layout/SearchBar'
import Header from '../sections/Header'
import Categories from '../sections/Categories'
import RestaurantItems from '../sections/RestaurantItems'

// style
import GlobalStyle from '../style/GlobalStyle'

const YELP_API_KEY =
    'rUGupgMisTmBdIrTZGxMKOw2jv0jO2F4ppdCUG2JVhYkoTv2CSou4o96PDiWaVJvOTACH072H2VnH4iIzlk5xWybVntO6AZlYFi-jZ_GHrzJqu3PcdFbTo1sglOAYXYx'

export default function Home() {
    const [restaurantData, setrestaurantData] = useState()

    const getRestaurantsFromYelp = () => {
        const yelpurl =
            'https://api.yelp.com/v3/businesses/search?term=restaurants&location=SanDiego'

        const apiOptions = {
            headers: {
                Authorization: `Bearer ${YELP_API_KEY}`,
               AccessControlAllowOrigin: 'http://localhost:3000',
// Access-Control-Allow-Credentials, 'true',
            },
        }

        return fetch(yelpurl, apiOptions)
            .then((res) => res.json())
            .then((json) => console.log(json));
    }

    useEffect(() => {
        getRestaurantsFromYelp();
    }, [])
    return (
        <SafeAreaView style={GlobalStyle.droidSafeArea}>
            <View style={{ backgroundColor: 'white', padding: 15 }}>
                <Header />
                <SearchBar />
            </View>
            <ScrollView showsVerticalScrollIndicator={false}>
                <Categories />
                <RestaurantItems />
            </ScrollView>
        </SafeAreaView>
    )
}
