import React from 'react'
import { View, Text, TouchableOpacity } from 'react-native'

// style
import RestaurantItemStyle from './style/RestaurantItemStyle'

// layouts
import RestaurantImage from '../layout/RestaurantImage'
import RestaurantInfo from '../layout/RestaurantInfo'

export const localRestaurants = []

const RestaurantItem = ({ restaurants }) => {
    return (
        <>
            <TouchableOpacity activeOpacity={1} style={{ marginBottom: 30 }}>
                {restaurants.map((restaurant, index) => {
                    return (
                        <View
                            key={index}
                            style={RestaurantItemStyle.RestaurantItem}
                        >
                            <RestaurantImage image={restaurants.image_url} />
                            <RestaurantInfo
                                name={restaurant.name}
                                rating={restaurant.rating}
                            />
                        </View>
                    )
                })}
            </TouchableOpacity>
        </>
    )
}

export default RestaurantItem
